import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Bookmark } from '../Models/bookmark.interface';
import { BookmarkService } from '../Services/bookmark.service';

@Component({
  selector: 'app-add-edit',
  templateUrl: './add-edit.component.html',
  styleUrls: ['./add-edit.component.css']
})
export class AddEditComponent {
  id: number;
  bookmarks: Bookmark | undefined;

  constructor(
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private bookmarkService: BookmarkService
  ) {
    this.id = Number(this.activatedRoute.snapshot.queryParamMap.get('id')) || 0;
    this.bookmarks = this.id !== 0 ? this.bookmarkService.getBookmarkById(this.id) : undefined;
  }

  onSubmit(): void {
    if (this.bookmarks) {
      if (this.bookmarks.id === 0) {
        // If the bookmark ID is 0, it means it's a new bookmark to be added
        this.bookmarkService.addBookmark(this.bookmarks);
      } else {
        // If the bookmark ID is not 0, it means it's an existing bookmark to be updated
        this.bookmarkService.updateBookmark(this.bookmarks);
      }

      // Navigate to the bookmark detail page after successful submission
      this.router.navigate(['/home', this.bookmarks.id]);
    }
  }
}
<link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">

<div class="container mt-4">
  <h2>Add Bookmark</h2>
  <form>
    <div class="mb-3">
      <label for="name" class="form-label">Name:</label>
      <input type="text" id="name" class="form-control" required>
    </div>

    <div class="mb-3">
      <label for="url" class="form-label">URL:</label>
      <input type="text" id="url" class="form-control" required>
    </div>

    <div class="mb-3">
      <label for="category" class="form-label">Category:</label>
      <select id="category" class="form-select" required>
        <option value="" disabled selected>Select Category</option>
        <option value="category1">Category 1</option>
        <option value="category2">Category 2</option>
      </select>
    </div>

    <div class="mb-3">
      <label for="language" class="form-label">Language:</label>
      <select id="language" class="form-select" required>
        <option value="" disabled selected>Select Language</option>
        <option value="language1">Language 1</option>
        <option value="language2">Language 2</option>
      </select>
    </div>

    <div class="mb-3">
      <label for="keywords" class="form-label">Keywords (separated by comma):</label>
      <input type="text" id="keywords" class="form-control" required>
    </div>

    <button type="submit" class="btn btn-primary">Submit</button>
  </form>
</div>
