import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Bookmark } from '../Models/bookmark.interface';
import { BookmarkService } from '../Services/bookmark.service';

@Component({
  selector: 'app-add-edit',
  templateUrl: './add-edit.component.html',
  styleUrls: ['./add-edit.component.css']
})

export class AddEditComponent {
  bookmark: Bookmark = {
    id: 0,
    name: '',
    category: '',
    language: '',
    dateAdded: new Date()
    keywords: '';
    url: '';
  };

  constructor(
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private bookmarkService: BookmarkService
  ) { }

  onSubmit(form: any): void {
    if (form.valid) {
      this.bookmarkService.addBookmark(this.bookmark).subscribe(
        (newBookmark: Bookmark) => {
          console.log('Bookmark added successfully:', newBookmark);
          form.reset();
        },
        (error) => {
          console.error('Error adding bookmark:', error);
        }
      );
    }
  }
}
<link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">

<div class="container mt-4">
  <h2>Add Bookmark</h2>
  <form #bookmarkForm="ngForm" (ngSubmit)="onSubmit(bookmarkForm)">
    <div class="mb-3">
      <label for="name" class="form-label">Name:</label>
      <input type="text" id="name" class="form-control" name="name" required [(ngModel)]="bookmark.name">
    </div>

    <div class="mb-3">
      <label for="url" class="form-label">URL:</label>
      <input type="text" id="url" class="form-control" name="url" required [(ngModel)]="bookmark.url">
    </div>

    <div class="mb-3">
      <label for="category" class="form-label">Category:</label>
      <select id="category" class="form-select" name="category" required [(ngModel)]="bookmark.category">
        <option value="" disabled selected>Select Category</option>
        <option value="category1">Category 1</option>
        <option value="category2">Category 2</option>
      </select>
    </div>

    <div class="mb-3">
      <label for="language" class="form-label">Language:</label>
      <select id="language" class="form-select" name="language" required [(ngModel)]="bookmark.language">
        <option value="" disabled selected>Select Language</option>
        <option value="language1">Language 1</option>
        <option value="language2">Language 2</option>
      </select>
    </div>

    <div class="mb-3">
      <label for="keywords" class="form-label">Keywords (separated by comma):</label>
      <input type="text" id="keywords" class="form-control" name="keywords" required [(ngModel)]="bookmark.keywords">
    </div>

    <button type="submit" class="btn btn-primary">Submit</button>
  </form>
</div>
